%  Plot the first four Taylor polynomials for exp(x).% Symbolic Differentiation of the Functionsyms yfy = exp(1-y^2);f1y = diff(fy);f2y = diff(fy,2);f3y = diff(fy,3);f4y = diff(fy,4);% Computed values of derivatives within range of xx = -1:.01:3;                   % Set x valuesfx = exp(1-x.^2);f1x = double(subs(f1y,x));f2x = double(subs(f2y,x));f3x = double(subs(f3y,x));f4x = double(subs(f4y,x));% Computed Taylor Polynomials up to 4th degreep0 = ones(size(x));p1 = p0 + (x-1).*f1x;p2 = p1 + ((x-1).^2/2).*f2x;p3 = p2 + ((x-1).^3/6).*f3x;p4 = p3 + ((x-1).^4/factorial(4)).*f4x;% Computed Taylor Polynomial up to nth degreepn = p0;pArr = zeros(1,4);for i = 1:30    pn = pn + ((x-1).^i/factorial(i)).*double(subs(diff(fy,i),x));end% x = -1:.01:3;                   % Set x values% fx = exp(1-x.^2);% f1x = -2*x.*exp(1 - x.^2);% f2x = 4*x.^2.*exp(1 - x.^2) - 2*exp(1 - x.^2);% f3x = 12*x.*exp(1 - x.^2) - 8*x.^3.*exp(1 - x.^2);% f4x = 12*exp(1 - x.^2) - 48*x.^2.*exp(1 - x.^2) + 16*x.^4.*exp(1 - x.^2);% p0 = ones(size(x));               % Compute Taylor polynomials% p1 = p0 + (x-1).*f1x;% p2 = p1 + ((x-1).^2/2).*f2x;% p3 = p2 + ((x-1).^3/6).*f3x;% p4 = p3 + ((x-1).^4/factorial(4)).*f4x;% Plot of Taylor Polynomials against functionfig1 = figure('position',[ 0 0 700 700 ]);movegui(fig1, 'center');subplot(4,2,1)plot(x,fx,'--', x,p0,'-');legend('f(x)','P_0(x)')title('plot of P_0(x) and f(x)')subplot(4,2,2)plot(x,fx,'--',x,p1,'-');legend('f(x)','P_1(x)')title('plot of P_1(x) and f(x)')subplot(4,2,3)plot(x,fx,'--',x,p2,'-');legend('f(x)','P_2(x)')title('plot of P_2(x) and f(x)')subplot(4,2,4)plot(x,fx,'--',x,p3,'-');legend('f(x)','P_3(x)')title('plot of P_3(x) and f(x)')subplot(4,2,[5,6])plot(x,fx,'--',x,p4,'-');legend('f(x)','P_4(x)')title('plot of P_4(x) and f(x)')subplot(4,2,[7,8])plot(x,fx,'--',x,pn,'-');legend('f(x)','P_n(x)')title('plot of P_n(x) and f(x)')