%  Plot the first four Taylor polynomials for exp(x).syms xx = -1:.01:3;                   % Set x valuesfx = exp(1-x.^2);dfx = -2*x.*exp(1 - x.^2);ddfx = 4*x.^2.*exp(1 - x.^2) - 2*exp(1 - x.^2);dddfx = 12*x.*exp(1 - x.^2) - 8*x.^3.*exp(1 - x.^2);ddddfx = 12*exp(1 - x.^2) - 48*x.^2.*exp(1 - x.^2) + 16*x.^4.*exp(1 - x.^2);p0 = ones(size(x));               % Compute Taylor polynomialsp1 = p0 + (x-1).*dfx;p2 = p1 + ((x-1).^2/2).*ddfx;p3 = p2 + ((x-1).^3/6).*dddfx;p4 = p3 + ((x-1).^4/factorial(4)).*ddddfx;subplot(2,3,1)plot(x,fx,'--', x,p0,'-');legend('f(x)','P_0(x)')title('plot of P_0(x) and f(x)')subplot(2,3,2)plot(x,fx,'--',x,p1,'-');legend('f(x)','P_1(x)')title('plot of P_1(x) and f(x)')subplot(2,3,3)plot(x,fx,'--',x,p2,'-');legend('f(x)','P_2(x)')title('plot of P_2(x) and f(x)')subplot(2,3,4)plot(x,fx,'--',x,p3,'-');legend('f(x)','P_3(x)')title('plot of P_3(x) and f(x)')subplot(2,3,5)plot(x,fx,'--',x,p4,'-');legend('f(x)','P_4(x)')title('plot of P_4(x) and f(x)')